import React from 'react';

const NewsCard = ({ article }) => {
  // Format the date
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  // Default image if none is provided
  const defaultImage = 'https://via.placeholder.com/300x200?text=No+Image+Available';

  return (
    <div className="card h-100">
      <img 
        src={article.urlToImage || defaultImage} 
        className="card-img-top" 
        alt={article.title}
        onError={(e) => {e.target.src = defaultImage}}
        style={{ height: '200px', objectFit: 'cover' }}
      />
      <div className="card-body">
        <h5 className="card-title">{article.title}</h5>
        <p className="card-text text-secondary">
          {article.source?.name} â€¢ {formatDate(article.publishedAt)}
        </p>
        <p className="card-text">{article.description}</p>
      </div>
      <div className="card-footer bg-transparent border-top-0">
        <a href={article.url} className="btn btn-primary w-100" target="_blank" rel="noopener noreferrer">
          Read Full Article
        </a>
      </div>
    </div>
  );
};

export default NewsCard;
