import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [showForgotPassword, setShowForgotPassword] = useState(false);
  const [forgotEmail, setForgotEmail] = useState('');
  const [captchaVerified, setCaptchaVerified] = useState(false);
  const recaptchaRef = useRef(null);

  const navigate = useNavigate();

  // Check if user is already logged in
  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user'));
    if (user) {
      navigate('/');
    }
  }, [navigate]);

  // Ensure reCAPTCHA is rendered after component mounts
  useEffect(() => {
    // Reset reCAPTCHA when toggling between login and forgot password
    if (window.grecaptcha && window.grecaptcha.reset) {
      window.grecaptcha.reset();
    }
  }, [showForgotPassword]);

  const { email, password } = formData;

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onCaptchaChange = (value) => {
    setCaptchaVerified(!!value);
  };

  const resetCaptcha = () => {
    if (recaptchaRef.current) {
      // Reset the reCAPTCHA widget
      window.grecaptcha.reset(recaptchaRef.current);
      setCaptchaVerified(false);
    }
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    if (!captchaVerified) {
      setError('Please verify that you are not a robot');
      setLoading(false);
      return;
    }

    try {
      const response = await axios.post('/api/users/login', {
        email,
        password,
        recaptchaToken: window.grecaptcha.getResponse()
      });

      // Save user to localStorage
      localStorage.setItem('user', JSON.stringify(response.data));
      
      setLoading(false);
      navigate('/');
    } catch (error) {
      setLoading(false);
      resetCaptcha();
      setError(
        error.response?.data?.message || 'An error occurred during login'
      );
    }
  };

  const handleForgotPassword = async (e) => {
    e.preventDefault();
    setError('');
    setMessage('');
    
    if (!forgotEmail) {
      setError('Please provide your email address');
      return;
    }
    
    setLoading(true);
    
    if (!captchaVerified) {
      setError('Please verify that you are not a robot');
      setLoading(false);
      return;
    }
    
    try {
      const response = await axios.post('/api/users/forgot-password', {
        email: forgotEmail,
      });
      
      setMessage(response.data.message || 'Reset instructions sent to your email.');
      setLoading(false);
    } catch (error) {
      setLoading(false);
      setError(
        error.response?.data?.message || 'Failed to process request'
      );
    }
  };

  return (
    <div className="row justify-content-center">
      <div className="col-md-6">
        <div className="card p-4">
          <div className="card-body">
            {!showForgotPassword ? (
              <>
                <h1 className="text-center mb-4">Login</h1>
                
                {error && <div className="alert alert-danger">{error}</div>}
                
                <form onSubmit={onSubmit}>
                  <div className="mb-3">
                    <label htmlFor="email" className="form-label">Email</label>
                    <input
                      type="email"
                      className="form-control"
                      id="email"
                      name="email"
                      value={email}
                      onChange={onChange}
                      required
                    />
                  </div>
                  
                  <div className="mb-3">
                    <label htmlFor="password" className="form-label">Password</label>
                    <input
                      type="password"
                      className="form-control"
                      id="password"
                      name="password"
                      value={password}
                      onChange={onChange}
                      required
                    />
                  </div>
                  
                  <div className="mb-3 recaptcha-container">
                    <div 
                      className="g-recaptcha" 
                      data-sitekey="6LeIxAcTAAAAAJcZVRqyHh71UMIEGNQ_MXjiZKhI"
                      data-callback="onCaptchaChange"
                    ></div>
                  </div>
                  
                  <button 
                    type="submit" 
                    className="btn btn-primary w-100"
                    disabled={loading || !captchaVerified}
                  >
                    {loading ? 'Logging in...' : 'Login'}
                  </button>
                </form>
                
                <div className="d-flex justify-content-between mt-3">
                  <p>
                    Don't have an account? <Link to="/register">Register</Link>
                  </p>
                  <p>
                    <a 
                      href="#" 
                      onClick={(e) => {
                        e.preventDefault();
                        setShowForgotPassword(true);
                        setError('');
                      }}
                    >
                      Forgot Password?
                    </a>
                  </p>
                </div>
              </>
            ) : (
              <>
                <h1 className="text-center mb-4">Reset Password</h1>
                
                {error && <div className="alert alert-danger">{error}</div>}
                {message && <div className="alert alert-success">{message}</div>}
                
                <form onSubmit={handleForgotPassword}>
                  <div className="mb-3">
                    <label htmlFor="forgotEmail" className="form-label">Email</label>
                    <input
                      type="email"
                      className="form-control"
                      id="forgotEmail"
                      value={forgotEmail}
                      onChange={(e) => setForgotEmail(e.target.value)}
                      required
                    />
                  </div>
                  
                  <div className="mb-3 recaptcha-container">
                    <div 
                      className="g-recaptcha" 
                      data-sitekey="6LeIxAcTAAAAAJcZVRqyHh71UMIEGNQ_MXjiZKhI"
                      data-callback="onCaptchaChange"
                    ></div>
                  </div>
                  
                  <button 
                    type="submit" 
                    className="btn btn-primary w-100 mb-3"
                    disabled={loading || !captchaVerified}
                  >
                    {loading ? 'Sending...' : 'Send Reset Link'}
                  </button>
                  
                  <button 
                    type="button" 
                    className="btn btn-secondary w-100"
                    onClick={() => {
                      setShowForgotPassword(false);
                      setError('');
                      setMessage('');
                    }}
                  >
                    Back to Login
                  </button>
                </form>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

// Add global function for reCAPTCHA callback
window.onCaptchaChange = function(token) {
  // Find React component instance and update state
  const event = new CustomEvent('recaptchaVerified', { detail: { token } });
  document.dispatchEvent(event);
};

export default Login;
