import React, { useEffect, useState } from 'react';

const ReCaptcha = ({ onChange }) => {
  const [loaded, setLoaded] = useState(false);

  useEffect(() => {
    // Check if reCAPTCHA is already loaded
    if (window.grecaptcha && window.grecaptcha.render) {
      setLoaded(true);
    } else {
      // Add event listener for when reCAPTCHA loads
      window.onloadCallback = function() {
        setLoaded(true);
      };
    }

    // Listen for verification events
    const handleRecaptchaVerified = (e) => {
      if (onChange) {
        onChange(e.detail.token);
      }
    };
    
    document.addEventListener('recaptchaVerified', handleRecaptchaVerified);
    
    return () => {
      document.removeEventListener('recaptchaVerified', handleRecaptchaVerified);
    };
  }, [onChange]);

  return (
    <div className="recaptcha-container">
      <div 
        className="g-recaptcha" 
        data-sitekey="6LeIxAcTAAAAAJcZVRqyHh71UMIEGNQ_MXjiZKhI"
        data-callback="onCaptchaChange"
      ></div>
      {!loaded && <div>Loading CAPTCHA...</div>}
    </div>
  );
};

export default ReCaptcha;
