import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    password2: '',
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [captchaVerified, setCaptchaVerified] = useState(false);

  const navigate = useNavigate();

  // Check if user is already logged in
  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user'));
    if (user) {
      navigate('/');
    }
  }, [navigate]);

  // Ensure reCAPTCHA is rendered after component mounts
  useEffect(() => {
    if (window.grecaptcha && window.grecaptcha.reset) {
      window.grecaptcha.reset();
    }
  }, []);

  const { username, email, password, password2 } = formData;

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onCaptchaChange = (value) => {
    setCaptchaVerified(!!value);
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (password !== password2) {
      setError('Passwords do not match');
      return;
    }

    if (!captchaVerified) {
      setError('Please verify that you are not a robot');
      return;
    }

    setLoading(true);

    try {
      const response = await axios.post('/api/users', {
        username,
        email,
        password,
        recaptchaToken: window.grecaptcha.getResponse(),
      });

      // Save user to localStorage
      localStorage.setItem('user', JSON.stringify(response.data));

      setLoading(false);
      navigate('/');
    } catch (error) {
      setLoading(false);
      // Reset reCAPTCHA
      window.grecaptcha.reset();
      setCaptchaVerified(false);

      setError(
        error.response?.data?.message || 'An error occurred during registration'
      );
    }
  };

  return (
    <div className="row justify-content-center">
      <div className="col-md-6">
        <div className="card p-4">
          <div className="card-body">
            <h1 className="text-center mb-4">Register</h1>

            {error && <div className="alert alert-danger">{error}</div>}

            <form onSubmit={onSubmit}>
              <div className="mb-3">
                <label htmlFor="username" className="form-label">
                  Username
                </label>
                <input
                  type="text"
                  className="form-control"
                  id="username"
                  name="username"
                  value={username}
                  onChange={onChange}
                  required
                />
              </div>

              <div className="mb-3">
                <label htmlFor="email" className="form-label">
                  Email
                </label>
                <input
                  type="email"
                  className="form-control"
                  id="email"
                  name="email"
                  value={email}
                  onChange={onChange}
                  required
                />
              </div>

              <div className="mb-3">
                <label htmlFor="password" className="form-label">
                  Password
                </label>
                <input
                  type="password"
                  className="form-control"
                  id="password"
                  name="password"
                  value={password}
                  onChange={onChange}
                  required
                />
              </div>

              <div className="mb-3">
                <label htmlFor="password2" className="form-label">
                  Confirm Password
                </label>
                <input
                  type="password"
                  className="form-control"
                  id="password2"
                  name="password2"
                  value={password2}
                  onChange={onChange}
                  required
                />
              </div>

              <div className="recaptcha-container mb-3">
                <div
                  className="g-recaptcha"
                  data-sitekey="6LeIxAcTAAAAAJcZVRqyHh71UMIEGNQ_MXjiZKhI"
                  data-callback="onCaptchaChange"
                ></div>
              </div>

              <button
                type="submit"
                className="btn btn-primary w-100"
                disabled={loading || !captchaVerified}
              >
                {loading ? 'Registering...' : 'Register'}
              </button>
            </form>

            <p className="mt-3 text-center">
              Already have an account? <Link to="/login">Login</Link>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

// Listen for the custom event from the global handler defined in Login.js
useEffect(() => {
  const handleRecaptchaVerified = (e) => {
    setCaptchaVerified(true);
  };

  document.addEventListener('recaptchaVerified', handleRecaptchaVerified);

  return () => {
    document.removeEventListener('recaptchaVerified', handleRecaptchaVerified);
  };
}, []);

export default Register;
